(1,	'customer_order_received',	'Contribution Received: To Supporter',	'Thanks for your contribution!',	NULL,	NULL,	NULL,	'<p><strong>Dear [[bill_first_name]],</strong></p>\r\n<p>This email confirms that your <strong>Contribution #[[order_number]]</strong> for <strong>[[total_amount]]</strong>&nbsp;has been successfully processed on a [[bill_card_type]]&nbsp;card ending in [[bill_card_last_4]]. Your confirmation number is <strong>[[confirmation_number]]</strong>.</p>\r\n<p>You can also check on the status of your contribution by clicking \'Track My Contribution\' on the top of our website, or by <a href=\"[[trackingurl]]\">clicking here</a>.</p>\r\n<p>--<br />[[shoporganization]]<br />[[shopurl]]</p>',	0,	1,	'2012-01-01',	NULL,	1,	NULL,	NULL,	'2021-03-05 19:43:14',	1,	NULL,	NULL,	0,	0,	'Sent to all supporters who process a contribution through your site confirming their contribution was successfully processed.',	'Contributions'),
(2,	'admin_order_received',	'Contribution Received: To Admin',	'Contribution Received (#[[order_number]])',	NULL,	NULL,	NULL,	'<p>Hi Team,</p>\r\n<p>This is an administrative notification email confirming&nbsp;that<strong> Contribution #[[order_number]]</strong> for <strong>[[total_amount]]</strong> has been successfully processed.</p>\r\n<p>The contribution confirmation number is <strong>[[confirmation_number]]</strong></p>\r\n<p>[[invoice_table]]</p>\r\n<p><strong>More Details:</strong><br />Website: <a href=\"[[public_tracking_url]]\">View Contribution Details</a><br />Control Panel: <a href=\"[[admin_tracking_url]]\">View Contribution Details</a></p>\r\n<p>--<br />The Giveloud Team<br />http://givecloud.com | http://help.givecloud.com</p>',	0,	0,	'2012-01-01',	NULL,	1,	NULL,	NULL,	'2021-02-17 18:51:43',	1,	NULL,	NULL,	0,	0,	'Sent to your organization\'s team when a contribution is received.',	'Internal'),
(3,	'member_welcome',	'Supporter: Welcome',	'Welcome!',	'',	'',	'',	'<p><span style=\"font-size: 10pt;\">Hi&nbsp;[[first_name]],</span></p>\r\n<p><span style=\"font-size: 10pt;\">Thanks for creating an account at [[shop_url]]. &nbsp;Now, each time you visit our site, you can login and manage your own profile with us.</span></p>\r\n<p><span style=\"font-size: 10pt;\"></span><span style=\"font-size: 10pt;\">Your <strong>My Profile</strong> and <strong>My History</strong> screens are accessed in the drop-down menu that shows up under your \"Welcome, [[first_name]]\" name at the top left corner of your browser&nbsp;window.</span></p>\r\n<p><span style=\"font-size: 10pt;\">We look forward to seeing again and appreciate you taking the time to set up an account with us.</span></p>\r\n<p><span style=\"font-size: 10pt;\">--</span><br /><span style=\"font-size: 10pt;\">[[shoporganization]]</span><br /><span style=\"font-size: 10pt;\">[[shopurl]]</span></p>',	0,	1,	'2012-01-01',	NULL,	1,	NULL,	NULL,	'2016-09-23 12:30:09',	1,	NULL,	NULL,	0,	0,	'Sent to your supporters when they first create a profile.',	'Supporters'),
(4,	'member_profile_update',	'Supporter: Updated',	'Your profile has been updated',	'',	'',	'',	'<p><span style=\"font-size: 10pt;\">Dear [[first_name]],</span></p>\r\n<p><span style=\"font-size: 10pt;\">Your profile has been updated as follows:&nbsp;</span></p>\r\n<p><span style=\"font-size: 10pt;\">[[profile_updates]]</span></p>\r\n<p><span style=\"font-size: 10pt;\">If you did not make these changes, please notify us immediately.</span></p>\r\n<p><span style=\"font-size: 10pt;\">--</span><br /><span style=\"font-size: 10pt;\">[[shoporganization]]</span><br /><span style=\"font-size: 10pt;\">[[shopurl]]</span></p>',	0,	1,	'2012-01-01',	NULL,	1,	NULL,	NULL,	'2016-09-23 12:30:41',	1,	NULL,	NULL,	0,	0,	'Sent to your supporters when they update their profile.',	'Supporters'),
(5,	'member_password_reset',	'Supporter: Password Reset',	'Your Login Link',	NULL,	NULL,	NULL,	'<p><span style=\"font-size: 10pt;\">Hi [[first_name]],</span></p>\r\n<p><span style=\"font-size: 10pt;\">Click below to access your account in the next 2 hours:</span></p>\r\n<p><span style=\"font-size: 10pt;\"><a href=\"[[password_reset_link]]\">[[password_reset_link]]</a></span></p>\r\n<p><span style=\"font-size: 10pt;\">You\'ll be asked to update your password for the future.</span></p>\r\n<p><span style=\"font-size: 10pt;\">--</span><br /><span style=\"font-size: 10pt;\">[[shoporganization]]</span><br /><span style=\"font-size: 10pt;\">[[shopurl]]</span></p>',	0,	1,	'2012-01-01',	NULL,	1,	NULL,	NULL,	'2019-06-28 15:31:58',	1,	NULL,	NULL,	0,	0,	'Sent to your supporters when they need to reset their password.',	'Supporters'),
(6,	'customer_downloads',	'Contribution: eDownload Links',	'Your Downloads',	'',	'',	'',	'<p><span style=\"font-size: 10pt;\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://815393a849b74051d552-f0e6c8ff8d0647d5bbdb36d26d405888.ssl.cf2.rackcdn.com/newtheme/images/your-logo.png\" alt=\"\" width=\"236\" height=\"95\" /></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><strong>Your Downloads</strong></span></p>\r\n<p style=\"text-align: center;\"><strong><span style=\"font-size: 10pt;\">&nbsp;</span></strong></p>\r\n<p><span style=\"font-size: 10pt;\"></span></p>\r\n<p><span style=\"font-size: 10pt;\">Hi [[bill_first_name]],</span></p>\r\n<p><span style=\"font-size: 10pt;\">Here are the secure links to the files you recently purchased.</span><br /><br /><span style=\"font-size: 10pt;\">[[download_links]]</span><br /><br /><span style=\"font-size: 10pt;\">--</span><br /><span style=\"font-size: 10pt;\">[[shop_organization]]</span></p>',	0,	1,	NULL,	NULL,	1,	'2013-03-09 21:15:19',	1,	'2016-09-23 12:25:29',	1,	NULL,	NULL,	0,	0,	'Sent to supporters who have purchased digital downloads. This email contains the secured download URLS.',	'Contributions'),
(7,	'merchant_recurring_payment_processing_summary',	'Recurring Payment: Processing Summary',	'Recurring Payment: Processing Summary',	'[[email]]',	'',	'',	'<p>Successful payments: [[successful_payments]]<br />Total processed:&nbsp;[[successful_payments_amount]]<br />Failed Payments:&nbsp;[[failed_payments]]<br />Failed Total:&nbsp;[[failed_payments_amount]]</p>\r\n<p>[[failed_payments_table]]</p>\r\n<p><br /><br /><br /></p>',	1,	1,	'2012-01-01',	NULL,	1,	NULL,	NULL,	NULL,	NULL,	NULL,	NULL,	0,	0,	'Sent to your organization\'s team after recurring accounts have been processed. This email contains a summary of payments that succeeded and failed.',	'Internal'),
(8,	'customer_recurring_payment_failure',	'Recurring Payment: Payment Failure',	'Opps!  We need your help!',	'[[email]]',	'',	'',	'<p><span style=\"font-size: 10pt;\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://815393a849b74051d552-f0e6c8ff8d0647d5bbdb36d26d405888.ssl.cf2.rackcdn.com/newtheme/images/your-logo.png\" alt=\"\" width=\"236\" height=\"95\" /></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><strong>Problem With Your Payment</strong></span></p>\r\n<p style=\"text-align: center;\"><strong><span style=\"font-size: 10pt;\">&nbsp;</span></strong></p>\r\n<p><span style=\"font-size: 10pt;\">Dear [[first_name]],</span></p>\r\n<p><span style=\"font-size: 10pt;\">It looks like there was a problem with your payment last time we charged your card.</span></p>\r\n<p><span style=\"font-size: 10pt;\">Did you know you can update your credit card or banking information right from our website? <strong><a href=\"[[recurring_payments_url]]\">Give it a try</a></strong>.</span></p>\r\n<p><span style=\"font-size: 10pt;\">We hope this notification was helpful :) &nbsp;We look forward to your continued partnership.</span></p>\r\n<p><span style=\"font-size: 10pt;\">--</span><br /><span style=\"font-size: 10pt;\">[[shoporganization]]</span><br /><span style=\"font-size: 10pt;\">[[shopurl]]</span></p>',	0,	1,	'2012-01-01',	NULL,	1,	NULL,	NULL,	'2016-09-23 12:23:45',	1,	NULL,	NULL,	0,	0,	'Sent to supporters whose recurring payment failed.',	'Recurring Payments'),
(9,	'customer_payment_method_expiring',	'Payment Method: Expiring',	'Payment method is expiring soon',	'[[email]]',	'',	'',	'<p><span style=\"font-size: 10pt;\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://815393a849b74051d552-f0e6c8ff8d0647d5bbdb36d26d405888.ssl.cf2.rackcdn.com/newtheme/images/your-logo.png\" alt=\"\" width=\"236\" height=\"95\" /></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><strong>Your Payment Method Is Expiring Soon!</strong></span></p>\r\n<p style=\"text-align: center;\"><strong><span style=\"font-size: 10pt;\">&nbsp;</span></strong></p>\r\n<p><span style=\"font-size: 10pt;\">Dear [[first_name]],</span></p>\r\n<p><span style=\"font-size: 10pt;\">It looks like the&nbsp;payment method we have on file is expiring soon.</span></p>\r\n<p><span style=\"font-size: 10pt;\">Did you know you can update your credit card or banking information right from our website? <strong><a href=\"[[payment_methods_url]]\">Give it a try</a></strong>.</span></p>\r\n<p><span style=\"font-size: 10pt;\">We hope this notification was helpful :) &nbsp;We look forward to your continued partnership.</span></p>\r\n<p><span style=\"font-size: 10pt;\">--</span><br /><span style=\"font-size: 10pt;\">[[shoporganization]]</span><br /><span style=\"font-size: 10pt;\">[[shopurl]]</span></p>',	0,	1,	'2012-01-01',	NULL,	1,	NULL,	NULL,	'2016-09-23 12:22:25',	1,	NULL,	NULL,	0,	0,	'Sent to supporters whose recurring payment method is expiring soon.',	'Recurring Payments'),
(10,	'customer_payment_method_expired',	'Payment Method: Expired',	'Expired Credit Card',	'[[email]]',	'',	'',	'<p><span style=\"font-size: 10pt;\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://815393a849b74051d552-f0e6c8ff8d0647d5bbdb36d26d405888.ssl.cf2.rackcdn.com/newtheme/images/your-logo.png\" alt=\"\" width=\"236\" height=\"95\" /></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 12pt;\"><strong>Your Payment Method Expired!</strong></span></p>\r\n<p style=\"text-align: center;\"><strong><span style=\"font-size: 10pt;\">&nbsp;</span></strong></p>\r\n<p><span style=\"font-size: 10pt;\">Dear [[first_name]],</span></p>\r\n<p><span style=\"font-size: 10pt;\">It looks like the&nbsp;payment method we have on file expired!</span></p>\r\n<p><span style=\"font-size: 10pt;\">Did you know you can update your credit card or banking information right from our website? <strong><a href=\"[[payment_methods_url]]\">Give it a try</a></strong>.</span></p>\r\n<p><span style=\"font-size: 10pt;\">We hope this notification was helpful :) &nbsp;We look forward to your continued partnership.</span></p>\r\n<p><span style=\"font-size: 10pt;\">--</span><br /><span style=\"font-size: 10pt;\">[[shoporganization]]</span><br /><span style=\"font-size: 10pt;\">[[shopurl]]</span></p>',	0,	1,	'2012-01-01',	NULL,	1,	NULL,	NULL,	'2016-09-23 12:24:19',	1,	NULL,	NULL,	0,	0,	'Sent to supporters whose recurring payment method has expired.',	'Recurring Payments'),
(11,	'customer_tax_receipt',	'Tax Receipt: To Customer',	'Your Tax Receipt',	'',	'',	'',	'<p>Hi [[first_name]],</p>\r\n<p>Thank you so much for your generosity! &nbsp;Attached you\'ll find your Tax Receipt <strong>[[number]]</strong> issued <strong>[[issued_at]]</strong> in the amount of <strong>$[[amount]]</strong>.</p>\r\n<p><span>--</span><br /><span>[[shoporganization]]</span><br /><span>[[shopurl]]</span></p>',	0,	1,	'2012-01-01',	NULL,	1,	NULL,	NULL,	'2016-09-23 12:00:04',	1,	NULL,	NULL,	0,	0,	'Sent to supporters after donating against a product that has tax receipts enabled. This email contains an attached tax receipt PDF.',	'Contributions'),
(13,	'customer_recurring_payment_success',	'Recurring Payment: Payment Success',	'Your payment has been processed!',	'[[email]]',	'',	'',	'<p><span style=\"font-size: 10pt;\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://815393a849b74051d552-f0e6c8ff8d0647d5bbdb36d26d405888.ssl.cf2.rackcdn.com/newtheme/images/your-logo.png\" alt=\"\" width=\"236\" height=\"95\" /></span></p>\r\n<p style=\"text-align: center;\"><strong><span style=\"font-size: 10pt;\">&nbsp;</span></strong></p>\r\n<p><span style=\"font-size: 10pt;\">Dear [[first_name]],</span></p>\r\n<p><span style=\"font-size: 10pt;\">Your payment of&nbsp;[[payment_amt]] has been processed successfully.</span></p>\r\n<p><span style=\"font-size: 10pt;\">You can <a href=\"[[recurring_payments_url]]\"><strong>login to your account</strong></a> at any time to view or manage your recurring payments.</span></p>\r\n<p><span style=\"font-size: 10pt;\">We appreciate&nbsp;your continued partnership.</span></p>\r\n<p><span style=\"font-size: 10pt;\">--</span><br /><span style=\"font-size: 10pt;\">[[shoporganization]]</span><br /><span style=\"font-size: 10pt;\">[[shopurl]]</span></p>',	0,	1,	'',	NULL,	1,	NULL,	NULL,	'2017-09-08 18:24:06',	1,	NULL,	NULL,	0,	0,	'Sent to supporters whose recurring payment succeeded.',	'Recurring Payments'),
(14,	'customer_recurring_payment_reminder',	'Recurring Payment: Reminder',	'Recurring payment reminder',	'[[email]]',	'',	'',	'<p><span style=\"font-size: 10pt;\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://815393a849b74051d552-f0e6c8ff8d0647d5bbdb36d26d405888.ssl.cf2.rackcdn.com/newtheme/images/your-logo.png\" alt=\"\" width=\"236\" height=\"95\" /></span></p>\r\n<p style=\"text-align: center;\"><strong><span style=\"font-size: 10pt;\">&nbsp;</span></strong></p>\r\n<p><span style=\"font-size: 10pt;\">Dear [[first_name]],</span></p>\r\n<p><span style=\"font-size: 10pt;\">This is a reminder that a recurring payment of&nbsp;[[payment_amt]] will&nbsp;be processed on [[profile_next_bill_date]].</span></p>\r\n<p><span style=\"font-size: 10pt;\">You can <a href=\"[[recurring_payments_url]]\"><strong>login to your account</strong></a> at any time to view or manage your recurring payments.</span></p>\r\n<p><span style=\"font-size: 10pt;\">We appreciate&nbsp;your continued partnership.</span></p>\r\n<p><span style=\"font-size: 10pt;\">--</span><br /><span style=\"font-size: 10pt;\">[[shoporganization]]</span><br /><span style=\"font-size: 10pt;\">[[shopurl]]</span></p>',	0,	1,	'',	NULL,	1,	NULL,	NULL,	'2017-09-11 13:55:00',	1,	NULL,	NULL,	0,	0,	'Sent to supporters three days before a recurring payment is processed.',	'Recurring Payments'),
(15,	'sponsorship_started',	'Sponsorship: Started',	'Thank you for your sponsorship',	'[[email]]',	'',	'',	'<p style=\"text-align: center;\"><strong><img src=\"https://815393a849b74051d552-f0e6c8ff8d0647d5bbdb36d26d405888.ssl.cf2.rackcdn.com/newtheme/images/your-logo.png\" alt=\"\" width=\"236\" height=\"95\" /></strong></p>\r\n<p style=\"text-align: center;\"><strong></strong></p>\r\n<p><strong>Dear<span>&nbsp;</span><span>[[first_name]]</span>,</strong></p>\r\n<p>Thank you for your sponsorship. Your sponsorship of&nbsp;<strong><span>[[sponsorship_first_name]]&nbsp;</span></strong><span>was started on&nbsp;<strong>[[sponsorship_start_date]]</strong></span><span>.</span></p>\r\n<p>Here is some information about&nbsp;<span>[[sponsorship_first_name]]</span></p>\r\n<p style=\"text-align: center;\">[[sponsorship_image]]</p>\r\n<p style=\"text-align: center;\">[[sponsorship_bio]]</p>\r\n<p></p>\r\n<p>You can login to your account to view your sponsorship details. If you have any questions regarding your sponsorship, please do not hesitate to contact us. &nbsp;</p>\r\n<p>--<br />[[shop_organization]]<br />[[shop_url]]</p>',	0,	1,	NULL,	NULL,	1,	NULL,	NULL,	'2017-10-19 15:07:55',	1,	NULL,	NULL,	0,	0,	'Sent to sponsors when a sponsorship has begun.',	'Sponsorship'),
(16,	'sponsorship_ended',	'Sponsorship: Ended',	'Your sponsorship was ended',	'[[email]]',	'',	'',	'<p><strong><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://815393a849b74051d552-f0e6c8ff8d0647d5bbdb36d26d405888.ssl.cf2.rackcdn.com/newtheme/images/your-logo.png\" alt=\"\" width=\"236\" height=\"95\" /></strong></p>\r\n<p><strong></strong></p>\r\n<p><strong>Dear<span>&nbsp;</span><span>[[first_name]]</span>,</strong></p>\r\n<p>This is to confirm that your&nbsp;sponsorship of&nbsp;<strong><span>[[sponsorship_first_name]]&nbsp;</span></strong><span>was ended&nbsp;on&nbsp;<strong>[[sponsorship_end_date]]</strong></span><span>.</span></p>\r\n<p>You can login to your account to view your sponsorship details. If you have any questions regarding your sponsorship, please do not hesitate to contact us. &nbsp;</p>\r\n<p>--<br />[[shop_organization]]<br />[[shop_url]]</p>',	0,	1,	NULL,	NULL,	1,	NULL,	NULL,	'2017-10-19 15:11:30',	1,	NULL,	NULL,	0,	0,	'Sent to sponsors when a sponsorship has ended.',	'Sponsorship'),
(17,	'sponsorship_timeline_updated',	'Sponsorship: Timeline Updated',	'Timeline updated!',	'[[email]]',	'',	'',	'<p><strong><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://815393a849b74051d552-f0e6c8ff8d0647d5bbdb36d26d405888.ssl.cf2.rackcdn.com/newtheme/images/your-logo.png\" alt=\"\" width=\"236\" height=\"95\" /></strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Dear&nbsp;[[first_name]],</strong></p>\r\n<p><strong>[[sponsorship_first_name]] \'s&nbsp;</strong>timeline has been updated.</p>\r\n<p><a href=\"[[sponsorship_url]]\">Check it out!</a></p>\r\n<p>--<br />[[shop_organization]]<br />[[shop_url]]</p>',	0,	1,	NULL,	NULL,	1,	NULL,	NULL,	NULL,	NULL,	NULL,	NULL,	0,	0,	'Sent to sponsors when the timeline of a child they are sponsoring is updated',	'Sponsorship'),
(18,	'fundraising_page_activated',	'Page Activated',	'Your Page Is LIVE!',	NULL,	NULL,	NULL,	'<p>Hi&nbsp;[[first_name]],</p>\r\n<p><strong>Congrats!&nbsp;</strong> Your page <strong>\"[[page_name]]\"</strong> is now LIVE!</p>\r\n<p>Share your link with your friends and start raising money towards your goal.</p>\r\n<p><strong>[[page_url]]</strong></p>\r\n<p>--<br />[[organization_name]]</p>',	0,	1,	'2012-01-01',	NULL,	1,	NULL,	NULL,	'2018-08-07 13:30:12',	1,	NULL,	NULL,	0,	0,	'Sent to the creator of a fundraiser when their page goes live.',	'Fundraising Pages'),
(19,	'fundraising_page_suspended',	'Page Suspended',	'Your Page Has Been Suspended',	NULL,	NULL,	NULL,	'<p>Hi&nbsp;[[first_name]],</p>\r\n<p>We regret to inform you that your page <strong>\"[[page_name]]\"</strong>&nbsp;has been suspended and removed from the site.</p>\r\n<p>If you think this was done in error, or you want to fix your page, please <a href=\"[[my_fundraising_pages_url]]\"><strong>log in</strong></a> and review your page.</p>\r\n<p>--<br />[[organization_name]]</p>',	0,	1,	'2012-01-01',	NULL,	1,	NULL,	NULL,	'2018-08-07 13:30:07',	1,	NULL,	NULL,	0,	0,	'Sent to the creator of a fundraiser when their page is suspended.',	'Fundraising Pages'),
(20,	'fundraising_page_abuse',	'Abuse Reported',	'Your Page Has Been Reported',	NULL,	NULL,	NULL,	'<p>Hi&nbsp;[[first_name]],</p>\r\n<p>Someone has reported your page,&nbsp;<strong>\"[[page_name]]\"</strong> with the following reason:</p>\r\n<p style=\"text-align: center;\"><strong>\"[[page_report_reason]]\"</strong></p>\r\n<p>Your page is at risk of being suspended.&nbsp;If you think this was done in error, or you want to fix your page, please <a href=\"[[my_fundraising_pages_url]]\"><strong>log in</strong></a> and review your page.</p>\r\n<p>--<br />[[organization_name]]</p>',	0,	1,	'2012-01-01',	NULL,	1,	NULL,	NULL,	'2018-08-07 13:29:45',	1,	NULL,	NULL,	0,	0,	'Sent to the creator of a fundraiser when their page is reported.',	'Fundraising Pages'),
(21,	'fundraising_page_donation_received',	'Donation Notification',	'Someone Donated!',	'[[email]]',	'',	'',	'<p><strong><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://815393a849b74051d552-f0e6c8ff8d0647d5bbdb36d26d405888.ssl.cf2.rackcdn.com/newtheme/images/your-logo.png\" alt=\"\" width=\"236\" height=\"95\" /></strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Hey&nbsp;[[first_name]],</strong></p>\r\n<p>A donation of <strong>[[total_amount]]</strong> was just made towards your fundraising page - [[page_name]].</p>\r\n<p>You\'ve now raised [[page_amount_raised]] towards your [[page_goal]] goal!</p>\r\n<p>&nbsp;</p><p>You can <a href=\"[[page_url]]\">click here</a> to view your page and you can edit it <a href=\"[[page_edit_url]]\">here</a>.</p><p>&nbsp;</p>\r\n<p>--<br />[[shop_organization]]<br />[[shop_url]]</p>',	0,	0,	NULL,	NULL,	1,	NULL,	NULL,	NULL,	NULL,	NULL,	NULL,	0,	0,	'Sent to the owner of a fundraising page when someone donates.',	'Fundraising Pages'),
(22,	'customer_pledge_received',	'Pledge Received: To Supporter',	'Thanks for your pledge!',	NULL,	NULL,	NULL,	'<p><strong>Dear [[bill_first_name]],</strong></p>\n<p>This email confirms that your <strong>Pledge #[[pledge_number]]</strong> for <strong>[[pledge_amount]]</strong> has been successfully received.</p>\n<p>--<br />[[organization_name]]<br />[[site_url]]</p>',	0,	0,	'2012-01-01',	NULL,	1,	'2020-08-17 21:40:49',	1,	'2020-08-17 21:47:07',	1,	NULL,	NULL,	0,	0,	'Sent to all supporters who make a pledge through your site confirming their pledge was successfully processed.',	'Contributions');

DROP TABLE IF EXISTS `external_references`;
CREATE TABLE `external_references` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `referenceable_type` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `referenceable_id` bigint(20) unsigned NOT NULL,
  `type` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `service` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `reference` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `external_reference_unique` (`referenceable_type`,`referenceable_id`,`type`,`service`),
  KEY `external_references_referenceable_type_referenceable_id_index` (`referenceable_type`,`referenceable_id`),
  KEY `external_references_reference_index` (`reference`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


DROP TABLE IF EXISTS `failed_jobs`;
CREATE TABLE `failed_jobs` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `connection` mediumtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `queue` mediumtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `payload` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `exception` longtext CHARACTER SET utf8 COLLATE utf8_unicode_ci,
