DROP TABLE IF EXISTS `payments`;
CREATE TABLE `payments` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT 'Unique identifier for the object.',
  `type` enum('card','bank','paypal','cheque','cash','unknown') COLLATE utf8mb4_unicode_ci NOT NULL,
  `livemode` tinyint(1) unsigned NOT NULL COMMENT 'Indicates whether the payment exists in live mode.',
  `status` enum('succeeded','pending','failed') COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'The status of the payment.',
  `amount` decimal(19,4) unsigned NOT NULL COMMENT 'Amount representing how much to charge.',
  `amount_refunded` decimal(19,4) unsigned NOT NULL COMMENT 'Amount refunded (can be less than the amount attribute on the payment if a partial refund was issued).',
  `currency` char(3) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Three-letter ISO currency code.',
  `paid` tinyint(1) unsigned NOT NULL COMMENT 'If the payment succeeded, or was successfully authorized for later capture.',
  `captured` tinyint(1) unsigned NOT NULL COMMENT 'If the payment was created without capturing, this Boolean represents whether it is still uncaptured or has since been captured.',
  `captured_at` datetime DEFAULT NULL,
  `refunded` tinyint(1) unsigned NOT NULL COMMENT 'Whether the payment has been fully refunded. If the payment is only partially refunded, this attribute will still be false.',
  `functional_currency_code` char(3) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `functional_exchange_rate` decimal(23,10) NOT NULL DEFAULT '1.0000000000',
  `functional_total` decimal(19,4) NOT NULL DEFAULT '0.0000',
  `reference_number` varchar(40) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Transaction ID on the gateway.',
  `description` text COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Often useful for displaying to users.',
  `statement_descriptor` varchar(25) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Extra information about a payment. This will appear on your customer''s statement.',
  `dispute` varchar(140) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Details about the dispute if the payment has been disputed.',
  `failure_code` varchar(40) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Error code explaining reason for payment failure if available',
  `failure_message` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Message to user further explaining reason for payment failure if available.',
  `outcome` enum('authorized','manual_review','issuer_declined','blocked','invalid') COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Details about whether the payment was accepted, and why.',
  `source_account_id` bigint(20) unsigned DEFAULT NULL COMMENT 'The account that this source belongs to. ',
  `source_payment_method_id` bigint(20) unsigned DEFAULT NULL,
  `gateway_type` varchar(40) COLLATE utf8mb4_unicode_ci DEFAULT 'NULL',
  `gateway_customer` varchar(64) COLLATE utf8mb4_unicode_ci DEFAULT 'NULL' COMMENT 'For Stripe a customer. For PaySafe a profile. For Vanco a customer.',
  `gateway_source` varchar(40) COLLATE utf8mb4_unicode_ci DEFAULT 'NULL' COMMENT 'For NMI a customer vault. For Stripe a card or bank account. For PayPal a billing agreement.',
  `card_funding` enum('credit','debit','prepaid','unknown') COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Card funding type.',
  `card_brand` enum('American Express','Carte Blanche','China UnionPay','Diners Club','Discover','Elo','JCB','Laser','Maestro','MasterCard','Solo','Switch','UnionPay','Visa','Unknown') COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Card brand.',
  `card_fingerprint` varchar(40) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Uniquely identifies this particular card number.',
  `card_last4` char(4) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'The last four digits of the card.',
  `card_exp_month` tinyint(2) unsigned DEFAULT NULL COMMENT 'Two-digit number representing the card’s expiration month.',
  `card_exp_year` smallint(4) unsigned DEFAULT NULL COMMENT 'Four-digit number representing the card’s expiration year.',
  `card_cvc_check` enum('pass','fail','unavailable','unchecked') COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'If a CVC was provided, results of the check.',
  `card_tokenization_method` enum('apple_pay','android_pay') COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'If the card number is tokenized, this is the method that was used.',
  `card_entry_type` enum('mag_stripe_reader','integrated_circuit_card','card_not_present') COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `card_verification` enum('signature','pin','pin_signature','failed','not_required') COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `card_wallet` varchar(12) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `card_country` char(2) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Two-letter ISO code representing the country of the card.',
  `card_name` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Cardholder name.',
  `card_address_line1` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Address line 1 (Street address/PO Box/Company name).',
  `card_address_line1_check` enum('pass','fail','unavailable','unchecked') COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Results of the check.',
  `card_address_line2` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Address line 2 (Apartment/Suite/Unit/Building)',
  `card_address_city` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'City/District/Suburb/Town/Village.',
  `card_address_state` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'State/County/Province/Region.',
  `card_address_zip` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'ZIP or postal code.',
  `card_address_zip_check` enum('pass','fail','unavailable','unchecked') COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Results of the check.',
  `card_address_country` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Billing address country, if provided when creating card.',
  `bank_name` varchar(40) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Name of the bank associated with the routing number.',
  `bank_fingerprint` varchar(40) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Uniquely identifies this particular bank account.',
  `bank_last4` char(4) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'The last four digits of the bank account number.',
  `bank_account_holder_name` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'The name of the person or business that owns the bank account.',
  `bank_account_holder_type` enum('individual','company') COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'The type of entity that holds the bank account.',
  `bank_account_type` enum('checking','savings') COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `bank_routing_number` varchar(40) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'The routing transit number for the bank account.',
  `cheque_number` varchar(40) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `cheque_date` date DEFAULT NULL,
  `signature` longtext COLLATE utf8mb4_unicode_ci,
  `ip_address` varchar(40) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `ip_country` char(2) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `platform_fee_type` varchar(32) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `stripe_payment_intent` varchar(64) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `payment_audit_log` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin,
  `created_at` datetime DEFAULT NULL,
  `updated_at` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `source_account_id` (`source_account_id`),
  KEY `source_payment_method_id` (`source_payment_method_id`),
  KEY `payments_platform_fee_type_index` (`platform_fee_type`),
  CONSTRAINT `payments_ibfk_2` FOREIGN KEY (`source_account_id`) REFERENCES `member` (`id`),
  CONSTRAINT `payments_ibfk_3` FOREIGN KEY (`source_payment_method_id`) REFERENCES `payment_methods` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


