DROP TABLE IF EXISTS `productcategory`;
CREATE TABLE `productcategory` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(250) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `description` mediumtext COLLATE utf8mb4_unicode_ci,
  `ismale` tinyint(1) DEFAULT NULL,
  `isfemale` tinyint(1) DEFAULT NULL,
  `sequence` int(10) unsigned DEFAULT NULL,
  `imageserverfile` varchar(500) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `media_id` bigint(20) unsigned DEFAULT NULL,
  `parent_id` bigint(20) unsigned DEFAULT NULL,
  `url_name` varchar(250) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `template_suffix` varchar(64) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `created_by` int(10) unsigned DEFAULT NULL,
  `created_at` datetime DEFAULT NULL,
  `updated_by` int(10) unsigned DEFAULT NULL,
  `updated_at` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `parent_id` (`parent_id`),
  KEY `media_id` (`media_id`),
  CONSTRAINT `productcategory_ibfk_1` FOREIGN KEY (`parent_id`) REFERENCES `productcategory` (`id`),
  CONSTRAINT `productcategory_ibfk_2` FOREIGN KEY (`media_id`) REFERENCES `media` (`id`) ON DELETE SET NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


DROP TABLE IF EXISTS `productcategorylink`;
CREATE TABLE `productcategorylink` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `productid` bigint(20) unsigned NOT NULL,
  `categoryid` bigint(20) unsigned NOT NULL,
  PRIMARY KEY (`id`),
  KEY `productid` (`productid`),
  KEY `categoryid` (`categoryid`),
  CONSTRAINT `productcategorylink_ibfk_3` FOREIGN KEY (`productid`) REFERENCES `product` (`id`) ON DELETE CASCADE,
  CONSTRAINT `productcategorylink_ibfk_4` FOREIGN KEY (`categoryid`) REFERENCES `productcategory` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


DROP TABLE IF EXISTS `productfields`;
CREATE TABLE `productfields` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `productid` bigint(20) unsigned DEFAULT NULL,
