DROP TABLE IF EXISTS `recurring_payment_profiles`;
CREATE TABLE `recurring_payment_profiles` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `status` varchar(14) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Status of the recurring payment profile. It is one of the following values: Active, Pending, Cancelled, Suspended, Expired.',
  `is_manual` tinyint(1) NOT NULL DEFAULT '0',
  `is_locked` tinyint(1) NOT NULL DEFAULT '0',
  `profile_id` varchar(14) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'A unique identifier for future reference to the details of this recurring payment.',
  `member_id` bigint(20) unsigned NOT NULL COMMENT 'The Member ID of the member owning the profile.',
  `paypal_subscription_id` varchar(40) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `stripe_subscription_id` varchar(40) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `subscriber_name` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Full name of the person receiving the product or service paid for by the recurring payment.',
  `profile_start_date` datetime NOT NULL COMMENT 'The date when billing for this profile begins.',
  `profile_reference` varchar(128) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'The merchant''s own unique reference or invoice number.',
  `description` varchar(128) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Description of the recurring payment.',
  `final_payment_due_date` datetime DEFAULT NULL COMMENT 'Final scheduled payment due date before the profile expires.',
  `aggregate_amount` decimal(19,4) NOT NULL DEFAULT '0.0000' COMMENT 'Total amount collected thus far for scheduled payments.',
  `max_failed_payments` tinyint(4) NOT NULL DEFAULT '1' COMMENT 'Number of scheduled payments that can fail before the profile is automatically suspended.',
  `auto_bill_out_amt` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Indicates whether you would like Donorshops to automatically bill the outstanding balance amount in the next billing cycle.',
  `nsf_fee` decimal(19,4) NOT NULL DEFAULT '0.0000' COMMENT 'NSF Fee to charge for failed payments.',
  `ship_to_name` varchar(32) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Person''s name associated with this shipping address.',
  `ship_to_street` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'First street address.',
  `ship_to_street2` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Second street address.',
  `ship_to_city` varchar(40) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Name of city.',
  `ship_to_state` varchar(40) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'State or province.',
  `ship_to_zip` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'U.S. ZIP code or other country-specific postal code.',
  `ship_to_country` varchar(2) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Country code.',
  `ship_to_phone_num` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Phone number.',
  `transaction_type` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'Standard' COMMENT 'The type of transaction. It is one of the following values: Standard, Donation.',
