  `billing_cycle_anchor` date DEFAULT NULL,
  `total_billing_cycles` int(11) NOT NULL DEFAULT '0' COMMENT 'Number of billing cycles for payment period.',
  `amt` decimal(19,4) NOT NULL COMMENT 'Billing amount for each billing cycle during this payment period. This amount does not include shipping and tax amounts.',
  `currency_code` char(3) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'USD' COMMENT 'Currency code (default is USD).',
  `shipping_amt` decimal(19,4) NOT NULL DEFAULT '0.0000' COMMENT 'Shipping amount for each billing cycle during this payment period.',
  `tax_amt` decimal(19,4) NOT NULL DEFAULT '0.0000' COMMENT 'Tax amount for each billing cycle during this payment period.',
  `init_amt` decimal(19,4) NOT NULL DEFAULT '0.0000' COMMENT 'Initial non-recurring payment amount due immediately upon profile creation.',
  `dcc_enabled_by_customer` tinyint(1) NOT NULL DEFAULT '0',
  `dcc_per_order_amount` decimal(19,4) NOT NULL DEFAULT '0.0000',
  `dcc_rate` decimal(19,4) NOT NULL DEFAULT '0.0000',
  `dcc_type` varchar(32) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `dcc_amount` decimal(19,4) NOT NULL DEFAULT '0.0000',
