  `next_attempt_date` date DEFAULT NULL COMMENT 'The next scheduled attempt to re-bill a failed payment.',
  `num_cycles_completed` smallint(6) NOT NULL DEFAULT '0' COMMENT 'The number of billing cycles completed in the current active subscription period.',
  `num_cycles_remaining` smallint(6) DEFAULT NULL COMMENT 'The number of billing cycles remaining in the current active subscription period.',
  `outstanding_balance` decimal(19,4) NOT NULL DEFAULT '0.0000' COMMENT 'The current past due or outstanding balance for this profile.',
  `failed_payment_count` smallint(6) NOT NULL DEFAULT '0' COMMENT 'The total number of failed billing cycles for this profile.',
