  `last_payment_amt` decimal(19,4) DEFAULT NULL COMMENT 'The amount of the last successful payment received for this profile.',
  `platform_fee_type` varchar(32) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `payment_method_id` bigint(20) unsigned DEFAULT NULL COMMENT 'The funding source to use in place of the default funding source.',
