  `productorderitem_id` bigint(20) unsigned NOT NULL COMMENT 'The Order Line Item ID in the original Order.',
  `productinventory_id` bigint(20) unsigned DEFAULT NULL COMMENT 'The Product Inventory ID of the ordered Product.',
  `product_id` bigint(20) unsigned DEFAULT NULL COMMENT 'The Product ID of the ordered Product.',
  `payment_mutex` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Mutex used while processing a recurring payment.',
  `sponsorship_id` int(10) unsigned DEFAULT NULL,
  `cancel_reason` varchar(64) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `dp_pledge_id_override` varchar(10) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`),
